mapper.select:
select
  a.state
from application_for_withdrawal a where a.id=#{id}

select
a.* ,IFNULL(w.money,0.00) as  balance   from application_for_withdrawal a
LEFT JOIN wallet w on w.user_id=a.user_id

  1=1

    and a.id = #{id}


    and a.user_name like concat('%',#{userName},'%')


    and a.state = #{state}


    and DATE_FORMAT(a.apply_time,'%Y-%m-%d') >=  #{startTime}


    and DATE_FORMAT(a.apply_time,'%Y-%m-%d') <=  #{endTime}


ORDER BY a.apply_time DESC

mapper.insert:
insert into application_for_withdrawal (id, user_id, phone,
  user_name, money, apply_time,
  update_time, state, reason
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{phone,jdbcType=VARCHAR},
  #{userName,jdbcType=VARCHAR}, #{money,jdbcType=DECIMAL}, #{applyTime,jdbcType=TIMESTAMP},
  #{updateTime,jdbcType=TIMESTAMP}, #{state,jdbcType=VARCHAR}, #{reason,jdbcType=VARCHAR}
  )
