mapper.select:
select
c.*,u.user_name as userName
from complaint as c
LEFT JOIN `user` as u on u.user_id=c.user_id

  1=1

    and c.phone = #{phone}


    and  DATE_FORMAT(c.create_time,'%Y-%m-%d') >=  #{startTime}


    and DATE_FORMAT(c.create_time,'%Y-%m-%d') <=  #{endTime}


    and c.user_id = #{userId}


    and u.user_name like  concat('%',#{userName},'%')


    and c.state = #{state}


ORDER BY c.create_time DESC

mapper.insert:
insert into complaint (com_id, id, create_time,
  user_id, title, content,
  use_time, state, phone,image_url
  )
values (#{comId,jdbcType=INTEGER}, #{id,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP},
  #{userId,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, #{content,jdbcType=VARCHAR},
  #{useTime,jdbcType=TIMESTAMP}, #{state,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR},#{imageUrl,jdbcType=VARCHAR}
  )
