mapper.select:
select e.*,q.c_name as educationName,m.categoryname as majorName
from education_exp as e
LEFT JOIN qs_category as q on e.education=q.c_id
LEFT JOIN qs_category_major m on m.id=e.major

  1=1

    and user_id = #{userId}


    and school like  concat('%',#{school},'%')


    and education = #{education}


    and major = #{major}


ORDER BY end_time DESC
