mapper.select:
select

from job_lighten_enum

    1=1

        and job_lighten_id = #{jobLightenId}


        and name like concat('%',#{name},'%')


        and state = #{state}


        and  DATE_FORMAT(create_time,'%Y-%m-%d') >=  #{startTime}


        and  DATE_FORMAT(create_time,'%Y-%m-%d') <=  #{endTime}


ORDER BY create_time DESC

mapper.update:
update job_lighten_enum
set id    = #{id,jdbcType=INTEGER},
    name  = #{name,jdbcType=VARCHAR},
    state = #{state,jdbcType=VARCHAR}
where job_lighten_id = #{jobLightenId,jdbcType=INTEGER}
