mapper.select:
select
l.* ,s.c_name as speakAbilityName,q.c_name as  writeAbilityName  ,d.c_name as languageName  from language as l
LEFT JOIN qs_category as s on s.c_id=l.speak_ability
LEFT JOIN qs_category as q on q.c_id=l.write_ability
LEFT JOIN qs_category as d on d.c_id=l.`language`

  1=1

    and l.id = #{id}


    and l.user_id = #{userId}


    and l.language =#{language}


    and l.speak_ability = #{speakAbility}


    and l.write_ability = #{writeAbility}

mapper.update:
update language
set user_id = #{userId,jdbcType=INTEGER},
  language = #{language,jdbcType=VARCHAR},
  speak_ability = #{speakAbility,jdbcType=VARCHAR},
  write_ability = #{writeAbility,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
