mapper.select:
select 

from point_detail
where user_id= #{userId,jdbcType=INTEGER}
and DATE_FORMAT(createtime,'%Y%m') = DATE_FORMAT(CURDATE(),'%Y%m')

  and flag= #{flag,jdbcType=INTEGER}

order by createtime desc

select

from point_detail
where user_id= #{userId,jdbcType=INTEGER}
and YEAR(createtime)=YEAR(NOW())

  and flag= #{flag,jdbcType=INTEGER}


order by createtime desc

mapper.insert:
insert into point_detail (id, user_id, point, 
  content, createtime, flag
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{point,jdbcType=INTEGER}, 
  #{content,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}, #{flag,jdbcType=INTEGER}
  )
