mapper.select:
select

from qs_category_jobs

  1=1

    and id = #{id}


    and parentid =#{parentid}


    and categoryname like  concat('%',#{categoryname},'%')

mapper.insert:
insert into qs_category_jobs (id, parentid, categoryname,
  category_order, stat_jobs, stat_resume,
  spell, relation1, relation1_cn,
  relation2, relation2_cn, content
  )
values (#{id,jdbcType=INTEGER}, #{parentid,jdbcType=SMALLINT}, #{categoryname,jdbcType=VARCHAR},
  #{categoryOrder,jdbcType=SMALLINT}, #{statJobs,jdbcType=VARCHAR}, #{statResume,jdbcType=VARCHAR},
  #{spell,jdbcType=VARCHAR}, #{relation1,jdbcType=VARCHAR}, #{relation1Cn,jdbcType=VARCHAR},
  #{relation2,jdbcType=VARCHAR}, #{relation2Cn,jdbcType=VARCHAR}, #{content,jdbcType=LONGVARCHAR}
  )
