mapper.select:
select

from rm_city

  1=1

    and uuid = #{uuid}


    and zh_label like  concat('%',#{zhLabel},'%')


    and province_id =#{provinceId}


    and stateflag =#{stateflag}

mapper.insert:
insert into rm_city (uuid, zh_label, province_id,
  creator, create_time, modifier,
  modify_time, time_stamp, stateflag
  )
values (#{uuid,jdbcType=VARCHAR}, #{zhLabel,jdbcType=VARCHAR}, #{provinceId,jdbcType=VARCHAR},
  #{creator,jdbcType=VARCHAR}, now(), #{modifier,jdbcType=VARCHAR},
  #{modifyTime,jdbcType=TIMESTAMP}, #{timeStamp,jdbcType=TIMESTAMP}, #{stateflag,jdbcType=INTEGER}
  )
