mapper.select:
select

from rc_user_role
where member_id = #{memberId,jdbcType=INTEGER}
  and role_id = #{roleId,jdbcType=INTEGER}

mapper.delete:
delete from rc_user_role
where member_id = #{memberId,jdbcType=INTEGER}
  and role_id = #{roleId,jdbcType=INTEGER}

mapper.update:
update rc_user_role


    state = #{state,jdbcType=VARCHAR},


where member_id = #{memberId,jdbcType=INTEGER}
  and role_id = #{roleId,jdbcType=INTEGER}

update rc_user_role
set create_time = now(),
  state = #{state,jdbcType=VARCHAR}
where member_id = #{memberId,jdbcType=INTEGER}
  and role_id = #{roleId,jdbcType=INTEGER}
